mapper.update:
update member
set mem_nick=#{mem_nick},
mem_pw=#{mem_pw},
mem_email=#{mem_email}
where mem_id=#{mem_id}

update member
set mem_grade='-1'
where mem_id=#{mem_id}

update member
set mem_login_state=#{mem_login_state}
where mem_id=#{mem_id}

